const { city } = input;
const { demand } = city;

const resourceUpdates = demand.map((x) => {
  const res = { ...x, type: x.type.id };
  const requestedQty = city.population * x.consumptionRate;
  if (x.quantity >= requestedQty) {
    res.quantity = x.quantity - requestedQty;
    res.ok = true;
  } else {
    res.ok = false;
  }
  return res;
});

return resourceUpdates.some((x) => !x.ok) ? [] : resourceUpdates;

const { city, cityUpdate, demandUpdates } = input;

const useMap = demandUpdates.length
  ? demandUpdates
  : city.demand.map((x) => ({
      ...x,
      type: x.type.id,
    }));

return useMap.map((x) => {
  const scarcity = cityUpdate.population / x.quantity;
  const adjustedPricePerUnit =
    x.basePricePerUnit + scarcity * x.scarcitySurchargeFactor;
  return {
    ...x,
    adjustedPricePerUnit,
  };
});

