const { sim, type, x, y } = input;

const filters = []

if (sim){
  filters.push({
    fieldName: "sim",
    op: "==",
    value: { ID: sim },
  })
}

if (type){
  filters.push({
    fieldName: "type",
    op: "==",
    value: { ID: type },
  })
}

if (x !== undefined) {
  filters.push({
    fieldName: "x",
    op: "==",
    value: { FLOAT: x },
  });
}

if (y !== undefined) {
  filters.push({
    fieldName: "y",
    op: "==",
    value: { FLOAT: y },
  });
}

return filters;

const { producer } = input;
const { products } = producer;

const resourceUpdates = products.map((x) => {
  const res = { ...x, type: x.type.id };
  const requestedQty = x.quantity + x.replenishRate;
  if (requestedQty <= x.capacity) {
    res.quantity = requestedQty;
    res.ok = true;
  } else {
    res.ok = false;
  }
  return res;
});

return resourceUpdates.some((x) => !x.ok) ? [] : resourceUpdates;

const { producer } = input;
const { material } = producer;

const resourceUpdates = material.map((x) => {
  const res = { ...x, type: x.type.id };
  const requestedQty = x.consumptionRate;
  if (x.quantity >= requestedQty) {
    res.quantity = x.quantity - requestedQty;
    res.ok = true;
  } else {
    res.ok = false;
  }
  return res;
});

return resourceUpdates.some((x) => !x.ok) ? [] : resourceUpdates;

